  struct FeeConfig {
    uint256 buyFee;
    uint256 sellFee;
    uint256 maxFee;
  }

  struct AntiBotConfig {
    uint256 antiBotDuration;
    uint256 maxBuyAmountEarly;
    uint256 maxWalletPercentage;
