pragma solidity 0.8.27;

interface ICarbonOpus {
    struct Song {
        address artist;
        uint256 price;
        uint256 referralPct;
    }

    error SongDoesNotExist(uint256 tokenId);
    error IncorrectPrice(uint256 expected, uint256 actual);
    error NoRewardsToClaim(address account);
    error TransferFailed();
    error NotArtist(address sender, uint256 tokenId);
    error ReferralPercentTooHigh(uint256 newPct);
    error InvalidFee(uint256 newFee);
    error InvalidAddress(address addr);
    error NotAuthorized(address sender);

    event RewardsClaimed(address indexed account, uint256 amount);
    event RewardsDistributed(address indexed artist, address indexed referrer, uint256 artistAmount, uint256 referrerAmount, uint256 protocolFee);
    event SongMinted(uint256 indexed tokenId, address indexed artist, uint256 price, uint256 referralPct);
    event SongPurchased(uint256 indexed tokenId, address indexed buyer, address indexed referrer, uint256 price);
    event SongPriceUpdated(uint256 indexed tokenId, uint256 newPrice);
    event SongReferralPctUpdated(uint256 indexed tokenId, uint256 newPct);
    event SongPriceScaled(uint256 indexed tokenId, uint256 newPrice);
    event ProtocolFeeUpdated(uint256 newFee);
    event TreasuryUpdated(address indexed newTreasury);
    event PriceScaleManagerUpdated(address indexed newManager);

    function mintMusic(uint256 price, uint256 referralPct) external;
    function purchaseMusic(uint256 tokenId, address referrer) external payable;
    function claimRewards() external;
    function musicBalance(address user) external view returns (uint256[] memory, uint256[] memory);

    function updateSongPrice(uint256 tokenId, uint256 newPrice) external;
    function updateSongReferralPct(uint256 tokenId, uint256 newPct) external;
    function updateProtocolFee(uint256 newFee) external;
    function updateTreasury(address newTreasury) external;
    function updatePriceScaleManager(address newManager) external;
    function scaleSongPrice(uint256 tokenId, uint256 newPrice) external;
    function setURI(string memory newUri) external;
}

contracts/interface/ISomniaExchangeRoute.content:
// SPDX-License-Identifier: MIT

pragma solidity >=0.6.2;

interface ISomniaExchangeRouter01 {
    function factory() external pure returns (address);
    function WETH() external pure returns (address);

    function addLiquidity(
        address tokenA,
        address tokenB,
        uint amountADesired,
        uint amountBDesired,
        uint amountAMin,
        uint amountBMin,
        address to,
        uint deadline
    ) external returns (uint amountA, uint amountB, uint liquidity);
    function addLiquidityETH(
        address token,
        uint amountTokenDesired,
        uint amountTokenMin,
        uint amountETHMin,
        address to,
        uint deadline
    ) external payable returns (uint amountToken, uint amountETH, uint liquidity);
    function removeLiquidity(
        address tokenA,
        address tokenB,
        uint liquidity,
        uint amountAMin,
        uint amountBMin,
        address to,
        uint deadline
    ) external returns (uint amountA, uint amountB);
    function removeLiquidityETH(
        address token,
        uint liquidity,
        uint amountTokenMin,
        uint amountETHMin,
        address to,
        uint deadline
    ) external returns (uint amountToken, uint amountETH);
    function removeLiquidityWithPermit(
        address tokenA,
        address tokenB,
        uint liquidity,
        uint amountAMin,
        uint amountBMin,
        address to,
        uint deadline,
        bool approveMax, uint8 v, bytes32 r, bytes32 s
    ) external returns (uint amountA, uint amountB);
    function removeLiquidityETHWithPermit(
        address token,
        uint liquidity,
        uint amountTokenMin,
        uint amountETHMin,
        address to,
        uint deadline,
        bool approveMax, uint8 v, bytes32 r, bytes32 s
    ) external returns (uint amountToken, uint amountETH);
    function swapExactTokensForTokens(
        uint amountIn,
        uint amountOutMin,
        address[] calldata path,
        address to,
        uint deadline
    ) external returns (uint[] memory amounts);
    function swapTokensForExactTokens(
        uint amountOut,
        uint amountInMax,
        address[] calldata path,
        address to,
        uint deadline
    ) external returns (uint[] memory amounts);
    function swapExactETHForTokens(uint amountOutMin, address[] calldata path, address to, uint deadline)
        external
        payable
        returns (uint[] memory amounts);
    function swapTokensForExactETH(uint amountOut, uint amountInMax, address[] calldata path, address to, uint deadline)
        external
        returns (uint[] memory amounts);
    function swapExactTokensForETH(uint amountIn, uint amountOutMin, address[] calldata path, address to, uint deadline)
        external
        returns (uint[] memory amounts);
    function swapETHForExactTokens(uint amountOut, address[] calldata path, address to, uint deadline)
        external
        payable
        returns (uint[] memory amounts);

    function quote(uint amountA, uint reserveA, uint reserveB) external pure returns (uint amountB);
    function getAmountOut(uint amountIn, uint reserveIn, uint reserveOut) external pure returns (uint amountOut);
    function getAmountIn(uint amountOut, uint reserveIn, uint reserveOut) external pure returns (uint amountIn);
    function getAmountsOut(uint amountIn, address[] calldata path) external view returns (uint[] memory amounts);
    function getAmountsIn(uint amountOut, address[] calldata path) external view returns (uint[] memory amounts);
}

interface ISomniaExchangeRouter02 is ISomniaExchangeRouter01 {
    function removeLiquidityETHSupportingFeeOnTransferTokens(
        address token,
        uint liquidity,
        uint amountTokenMin,
        uint amountETHMin,
        address to,
        uint deadline
    ) external returns (uint amountETH);
    function removeLiquidityETHWithPermitSupportingFeeOnTransferTokens(
        address token,
        uint liquidity,
        uint amountTokenMin,
        uint amountETHMin,
        address to,
        uint deadline,
        bool approveMax,
        uint8 v,
        bytes32 r,
        bytes32 s
    ) external returns (uint amountETH);

    function swapExactTokensForTokensSupportingFeeOnTransferTokens(
        uint amountIn,
        uint amountOutMin,
        address[] calldata path,
        address to,
        uint deadline
    ) external;
    function swapExactETHForTokensSupportingFeeOnTransferTokens(
        uint amountOutMin,
        address[] calldata path,
        address to,
        uint deadline
    ) external payable;
    function swapExactTokensForETHSupportingFeeOnTransferTokens(
        uint amountIn,
        uint amountOutMin,
        address[] calldata path,
        address to,
        uint deadline
    ) external;
}
